export const Task = (props) => {
    const deleteTask = (id) => {
        setTodoList(todoList.filter((task) => task.id !== id));
    };
    return (
        <div className="task">
      <h1>{props.taskName}</h1>
      <button onClick={() => deleteTask(props.id)}>x</button>
      </div>
      );
};
